export type Category = 'car' | 'bike' | 'room'

export type Item = {
  id: string
  category: Category
  title: string
  price_per_day: number
  images: string[]
  specs: Record<string, string | number>
  rating: number
  location: string
  description?: string
}

export type Booking = {
  id: string
  user_id: string
  item_id: string
  start_date: string
  end_date: string
  total_price: number
  status: 'pending' | 'confirmed' | 'cancelled'
}

