const defaultImageByCategory: Record<Category, string> = {
  car: '/images/car-lamborghini.svg',
  bike: '/images/bike-ducati.svg',
  room: '/images/room-ocean.svg',
}

const allowedLocations = ['sujanpur', 'hamirpur', 'kangra', 'goa'] as const
const pickAllowedLocation = (id: string, loc?: string): string => {
  const l = (loc || '').toLowerCase()
  if (allowedLocations.includes(l as any)) return l
  let sum = 0
  for (let i = 0; i < id.length; i++) sum += id.charCodeAt(i)
  return allowedLocations[sum % allowedLocations.length]
}

const normalizeItems = (items: Item[]): Item[] => {
  return items.map((i) => {
    const images = i.images && i.images.length ? i.images : [defaultImageByCategory[i.category] || placeholder]
    return {
      ...i,
      images,
      location: pickAllowedLocation(i.id, i.location),
    }
  })
}

const ensureMinCount = (items: Item[], category: Category, min = 10): Item[] => {
  let normalized = normalizeItems(items)

  // Overlay mock images if matching ID exists (to allow local image corrections)
  const categoryMocks = mockItems.filter((i) => i.category === category)
  normalized = normalized.map(item => {
    const mock = categoryMocks.find(m => m.id === item.id)
    if (mock && mock.images && mock.images.length > 0) {
      return { ...item, images: mock.images }
    }
    return item
  })

  if (normalized.length >= min) return normalized
  const existingIds = new Set(normalized.map((i) => i.id))
  const mocks = normalizeItems(categoryMocks)
  const filler = mocks.filter((i) => !existingIds.has(i.id))
  return [...normalized, ...filler].slice(0, min)
}

export const getCars = async (): Promise<Item[]> => {
  const { data, error } = await supabase.from('items').select('*').eq('category', 'car')
  if (error) {
    console.error(error)
    return ensureMinCount(mockItems.filter((i) => i.category === 'car'), 'car')
  }
  const list = data || []
  return ensureMinCount(list, 'car')
}

